def makeRelative(mat, length):
    retMat = {}
    for elem in mat:
        retMat[elem] = mat[elem]/length

    return retMat

allSubstMats = [makeRelative(x, protLengths[idx]) for idx, x in enumerate(allSubstMats)]
plot_data = 'rel'
yMaxVal = 0.0035
yaxistitle = 'Relative amount of substitutions'


from openpyxl import Workbook


import matplotlib
import matplotlib.pyplot as plt
import numpy as np
import matplotlib.cm as cm

matplotlib.rcParams['font.size'] = 22
matplotlib.rcParams['patch.linewidth'] = 3


figSize = (30, 12)
cols = len(sortedAA)
anchorX = 1.1


if len(allSubstMats) == 1:
    cols = 11
    anchorX = 0.5
    figSize = (15,12)

plt.style.use('seaborn-pastel')
fig, lax = plt.subplots(1, len(allSubstMats), figsize=figSize)



if len(allSubstMats) == 1:
    lax = [lax]


for mdx, subMatrix in enumerate(allSubstMats):

    wb = Workbook()
    ws = wb.active

    ws.append([""] + sortedAA)

    allValues = []

    for aaI in sortedAA:

        rowValues = []
        for aaJ in sortedAA:

            if aaI != aaJ:
                rowValues.append(subMatrix.get((aaI, aaJ), 0))
            else:
                rowValues.append(0)

        ws.append([aaI] + rowValues)
        allValues.append(rowValues)

    #wb.save("/mnt/c/Users/mjopp/Desktop/"+subMatrix2Org[mdx]+".xlsx")


    organism = "Helicobacter pylori" if mdx==0 else "Campylobacter jejuni"

    allP = []
    ind = [x for x in range(len(sortedAA))]

    accumVal = [0] * len(sortedAA)

    print(accumVal, len(accumVal))
    print(ind, len(ind))

    colors = iter(cm.tab20b(np.linspace(0, 1, len(sortedAA))))

    ax = lax[mdx]

    for idx,row in enumerate(allValues):

        p = ax.bar(ind, row, 0.35, bottom = accumVal, label=translateLegendName.get(sortedAA[idx], sortedAA[idx]), color=next(colors))

        allP.append(p)

        for idx, x in enumerate(row):
            accumVal[idx] += x


    print(accumVal)

    maxVal = ((max(accumVal)/10.0)+1.0) * 10

    #ax.set_yscale('log')
    #ax.set_ylim((pow(10, -8), 5*pow(10, 3)))
    ax.set_ylim((0, yMaxVal))

    ax.set_ylabel(yaxistitle)
    ax.set_xlabel('Changed amino acid')
    ax.set_title(organism + ': Substitutions by amino acid')
    ax.set_xticks(ind)
    ax.set_xticklabels([translateColumnName.get(x, x) for x in sortedAA])

left  = 0.125  # the left side of the subplots of the figure
right = 0.9    # the right side of the subplots of the figure
bottom = 0.2   # the bottom of the subplots of the figure
top = 0.9      # the top of the subplots of the figure
wspace = 0.2   # the amount of width reserved for blank space between subplots,
               # expressed as a fraction of the average axis width
hspace = 0.2   # the amount of height reserved for white space between subplots,
               # expressed as a fraction of the average axis height

fig.subplots_adjust(left=left, bottom=bottom, right=right, top=top,
                wspace=wspace, hspace=hspace)

lax[0].legend(loc='upper center', bbox_to_anchor=(anchorX, -0.1),
          fancybox=True, shadow=True, ncol=cols, prop={'size': 16})

#plt.axis('tight')
plt.savefig(fileLocation + "/plots/submatrix_"+ "_".join(subMatrix2Org) +"_"+plot_data+ ".png")
